$(function(){

    $(".rate-star").rateYo({
        starWidth: "15px",
        ratedFill: "#ffc000",
        numStars: 5,
        rating: 4.5,
        readOnly: true
    });   
    $(".weekly-products__slider").slick({
        slidesToShow: 1,
        slidesToScroll: 1,
        speed: 1000,
        arrows: true,
        prevArrow: '<img class="slider__arrow previous__arrow" src="images/icons/arrow-left.png" alt="">',
        nextArrow: '<img class="slider__arrow next__arrow" src="images/icons/arrow-right.png" alt="">',
    });   
    $(".feeds__slider").slick({
        slidesToShow: 3,
        slidesToScroll: 1,
        speed: 1000,
        arrows: true,
        prevArrow: '<img class="slider__arrow feeds__slider-arrow previous__arrow" src="images/icons/arrow-left.png" alt="">',
        nextArrow: '<img class="slider__arrow feeds__slider-arrow next__arrow" src="images/icons/arrow-right.png" alt="">',
    });  
    
    $(".js-range-slider").ionRangeSlider({
        type: "double",
        min: 0,
        max: 1000,
        from: 30,
        to: 400,
    });
    

    $(".lnr-list").on('click', function(){
        $(".newest__item").addClass('list');
        $(".lnr-list").addClass('active');
        $(".lnr-frame-expand").removeClass('active');

    });
    $(".lnr-frame-expand ").on('click', function(){
        $(".newest__item").removeClass('list');
        $(".lnr-frame-expand").addClass('active');
        $(".lnr-list").removeClass('active');
    });


    var mixer = mixitup('.newest__inner');

}); 


